generated.css:
@charset "UTF-8";
@import url("https://fonts.googleapis.com/css?family=Roboto:300,400,500,700");
/* 스타일 초기화 */
html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed,
figure, figcaption, footer, header, hgroup,
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
  margin: 0;
  padding: 0;
  border: 0;
  box-sizing: border-box;
  font-size: 1em;
  font-family: "Roboto", sans-serif;
  font-weight: 300;
  vertical-align: baseline; }

/* HTML5 display-role reset for older browsers */
/* 모르는 태그가 나오면 inline요소처리하는데, 해당 태그들를 block 요소로 만들어줌 */
article,
aside,
details,
figcaption,
figure,
footer,
header,
hgroup,
menu,
nav,
section,
main {
  display: block; }

ul {
  list-style-type: none; }

.wrapper {
  color: #fff;
  text-align: center;
  overflow: hidden;
  z-index: 5;
  background-color: #232323;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%; }

.wrapper h1 {
  padding: 20px 0;
  background-color: steelblue; }

.wrapper span {
  font-size: 3rem;
  font-weight: 400; }

.game {
  display: block;
  height: calc(100% - 190px);
  position: relative;
  z-index: 5; }

.score {
  padding-top: 15px; }

.option-container {
  position: relative;
  top: 50%;
  -webkit-transform: translateY(-50%);
  -ms-transform: translateY(-50%);
  transform: translateY(-50%);
  display: block; }

.color-text {
  font-size: 1.5rem;
  padding-bottom: 50px; }

.option {
  display: inline-block;
  border: 1px solid black;
  border-radius: 30px;
  width: 50px;
  height: 50px;
  margin: 0 10px; }

.right-modal {
  font-size: 3rem;
  font-weight: 700;
  display: none; }

.next-stage {
  margin-top: 50px;
  padding: 15px 10px; }

.wrong-modal {
  display: none; }

.right-modal.open {
  display: block; }

.wrong-modal.open {
  display: block; }
